query getStudent($id: ID!){
    estudiante (id: $id){
        id
        name
        email
    }
}

query getStudent($id: ID!, $id2: ID!){
    uno: estudiante (id: $id){
        ... datosEstudiante
    }
    dos: estudiante (id: $id2){
        ...datosEstudiante
    }
}

