query getCourse($curso: ID!)
    {
        curso (curso: $curso){
            id
            title
            description
            clases
            logo
            level
            path
            time
            students{
                id
                name
                email
            }
        }
    }

query getCourses($idcursoUno: ID!, $idcursoDos: ID!)
    {
        cursoUno: curso (curso: $idcursoUno){
            ... cursoFragment
        }

        cursoDos: curso (curso: $idcursoDos){
            ... cursoFragment           
        }
    }

    fragment cursoFragment on Curso {
        id
        title
        description
        clases
        logo
        level
        path
        time
        students{
            id
            name
            email
        }
    }

query getCourses($idcursoUno: ID!, $idcursoDos: ID!, $include: Boolean!)
    {
        cursoUno: curso (curso: $idcursoUno){
            ... cursoFragment
        }

        cursoDos: curso (curso: $idcursoDos){
            ... cursoFragment           
        }
    }

    fragment cursoFragment on Curso {
        id
        title @include(if: $include)
        description @include(if: $include)
        clases
        logo
        level
        path
        time
        students{
            id
            name
            email
        }
    }

query getCourses($idcursoUno: ID!, $idcursoDos: ID!, $skip: Boolean!)
    {
        cursoUno: curso (curso: $idcursoUno){
            ... cursoFragment
        }

        cursoDos: curso (curso: $idcursoDos){
            ... cursoFragment           
        }
    }

    fragment cursoFragment on Curso {
        id
        title @skip(if: $skip)
        description @skip(if: $skip)
        clases
        logo
        level
        path
        time
        students{
            id
            name
            email
        }
    }

mutation nuevoCurso($curso: CursoInput!){
  cursoNuevo(curso: $curso){
    id
    title,
    description   
  }
}

